import React, {Component} from 'react'
import './Game.css'
import SpymastersGame from './Spymasters/SpymastersGame.js'
import OperativesGame from './Operatives/OperativesGame.js'


class Game extends Component {
    constructor() {
        super()
        this.state = {
            spymaster: true,
            room_key: '',
            nickname: '',
            team: '',
            task: '',
            red_score: 0,
            blue_score: 0
        }
    }

    componentDidMount = () => {
        if (this.state.team === 'R') {
            axios.post('http://127.0.0.1:8000/codenames/redTeam', {
                game_id: this.state.gameid,
                connected_room_key: this.props.location.state.room_key
            })
        }
        else if (this.state.team === 'B') {
            axios.post('http://127.0.0.1:8000/codenames/blueTeam', {
                game_id: this.state.gameid,
                connected_room_key: this.props.location.state.room_key
            })
        }
        this.setState({
            room_key: this.props.location.state.room_key,
            nickname: this.props.location.state.nickname,
            team: this.props.location.state.team,
            task: this.props.location.state.task
        })
    }


    render() {
        return(
            <div>
                {
                    this.state.spymaster ?

                    <SpymastersGame 
                        room_key = {this.state.room_key}
                    />

                    : 

                    <OperativesGame 
                        room_key = {this.state.room_key}
                    />
                }
            </div>
        )
    }
}

export default Game
